using System.Collections; using System.Collections.Generic; using UnityEngine;  public class StationaryController : MonoBehaviour {     public GameObject childMonster1;      private int flag;     // Use this for initialization  void Start () {         flag = 0;   }       // Update is called once per frame  void Update () {         if (flag == 1){             childMonster1.SetActive(false);             gameObject.SetActive(false);         }     }      private void OnTriggerEnter(Collider other)     {         if (other.gameObject.CompareTag("Traveller")){             flag = 1;             travellerHealth th1 = other.gameObject.GetComponentInChildren<travellerHealth>();             if (th1 == null){                 Debug.Log("Could not find travellerHealth script");             }else{                 th1.TakeStationaryDamage();             }         }     } }  